/*!
 * Menu mobile semplificato - Versione funzionante garantita
 */

// Vanilla JavaScript senza dipendenze jQuery
document.addEventListener('DOMContentLoaded', function() {
    
    // Elementi del menu
    const hamburger = document.querySelector('.hamburger');
    const offcanvasNav = document.querySelector('.offcanvas-nav');
    const offcanvasClose = document.querySelector('.offcanvas-close');
    const body = document.body;
    
    // Verifica che gli elementi esistano
    if (!hamburger || !offcanvasNav) {
        console.log('Menu elements not found');
        return;
    }
    
    // Funzione per aprire/chiudere il menu
    function toggleMenu() {
        hamburger.classList.toggle('active');
        offcanvasNav.classList.toggle('open');
        body.classList.toggle('nav-open');
    }
    
    // Funzione per chiudere il menu
    function closeMenu() {
        hamburger.classList.remove('active');
        offcanvasNav.classList.remove('open');
        body.classList.remove('nav-open');
    }
    
    // Click sul hamburger
    hamburger.addEventListener('click', function(e) {
        e.preventDefault();
        e.stopPropagation();
        toggleMenu();
    });
    
    // Click sul bottone di chiusura X
    if (offcanvasClose) {
        offcanvasClose.addEventListener('click', function(e) {
            e.preventDefault();
            closeMenu();
        });
    }
    
    // Click sui link del menu per chiuderlo
    const menuLinks = offcanvasNav.querySelectorAll('a');
    menuLinks.forEach(function(link) {
        link.addEventListener('click', function() {
            closeMenu();
        });
    });
    
    // Click fuori dal menu per chiuderlo
    document.addEventListener('click', function(e) {
        if (offcanvasNav.classList.contains('open') && 
            !offcanvasNav.contains(e.target) && 
            !hamburger.contains(e.target)) {
            closeMenu();
        }
    });
    
    // Sticky navbar su scroll
    const navbar = document.querySelector('.navbar');
    if (navbar) {
        window.addEventListener('scroll', function() {
            if (window.scrollY > 100) {
                navbar.classList.add('navbar-sticky');
            } else {
                navbar.classList.remove('navbar-sticky');
            }
        });
    }
});